mapper.insert:
INSERT INTO BOARD(B_NO
				, B_TITLE
				, B_CONTENT
				, USER_NO
				, CATEGORY_MAIN
				, CATEGORY_SUB)
		   VALUES(SEQ_BOARD.NEXTVAL
		   		, #{boardTitle}
		   		, #{boardContent}
		   		, #{userNo}
		   		, #{categoryMain}
		   		, #{categorySub})

INSERT INTO B_ATTACHMENT(FILE_NO
                       , ORIGIN_NAME
                       , CHANGE_NAME
                       , FILE_PATH
                       , REF_BNO)
                VALUES(SEQ_B_ATTACHMENT.NEXTVAL
                     , #{originName}
                     , #{changeName}
                     , #{filePath}
                     , SEQ_BOARD.CURRVAL)

mapper.update:
UPDATE B_ATTACHMENT
SET STATUS = 'N',
WHERE REF_BNO = #{boardNo}

UPDATE REPLY
SET STATUS = 'N'
WHERE REP_NO = #{repNo}
