mapper.select:
	SELECT M.*, P.P_NO
	    FROM MEMBER M
	    LEFT JOIN PETSITTER P ON M.USER_NO = P.USER_NO
    WHERE M.USER_ID = #{userId}
        AND M.USER_PWD = #{userPwd}
        AND M.STATUS = 'Y'

mapper.insert:
INSERT INTO MEMBER(USER_NO
				 , USER_ID
				 , USER_NICKNAME
				 , USER_PWD
				 , USER_NAME
				 , USER_SSN
				 , PHONE
				 , EMAIL
				 , ADDRESS
				 , GENDER)
		  VALUES(SEQ_MEMBER.NEXTVAL
		  	   , #{userId}
		  	   , #{userNickname}
			   , #{userPwd}
			   , #{userName}
			   , #{userSsn}
			   , #{phone}
			   , #{email}
			   , #{address}
			   , #{gender})

mapper.select:
SELECT *
  FROM MEMBER
 WHERE USER_NAME = #{userName}
   AND EMAIL = #{email}

SELECT *
  FROM MEMBER
 WHERE USER_Id = #{userId}
   AND EMAIL = #{email}

mapper.update:
UPDATE MEMBER
   SET USER_PWD = #{userPwd}
	 , USER_NICKNAME = #{userNickname}
	 , EMAIL = #{email}
	 , PHONE = #{phone}
	 , ADDRESS = #{address}
 WHERE USER_ID = #{userId}

UPDATE MEMBER
	  SET STATUS = 'N'
WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO DOG(DOG_NO
			  , DOG_NAME
			  , DOG_GENDER
			  , DOG_BREED
			  , DOG_BIRTHDAY
			  , DOG_WEIGHT
			  , NEUTRALIZATION_STATUS
			  , DOG_NOTE
			  , FILE_PATH
			  , USER_NO)
		  VALUES(SEQ_DOG.NEXTVAL
		  	   , #{dogName}
		  	   , #{dogGender}
			   , #{dogBreed}
			   , #{dogBirthday}
			   , #{dogWeight}
			   , #{neutralizationStatus}
			   , #{dogNote}
			   , #{filePath}
			   , #{userNo})

mapper.select:
SELECT COUNT(*)
  FROM MEMBER
WHERE USER_ID = #{checkId}
