mapper.select:
SELECT *
  FROM MEMBER
 WHERE USER_ID = #{userId}
   AND USER_PWD = #{userPwd}
   AND STATUS = 'Y'

mapper.insert:
INSERT INTO MEMBER(USER_NO
				 , USER_ID
				 , USER_PWD
				 , USER_NAME
				 , USER_SSN
				 , PHONE
				 , EMAIL
				 , ADDRESS
				 , GENDER)
		  VALUES(SEQ_MEMBER.NEXTVAL
		  	   , #{userId}
			   , #{userPwd}
			   , #{userName}
			   , #{userSsn}
			   , #{phone}
			   , #{email}
			   , #{address}
			   , #{gender})

mapper.select:
SELECT *
  FROM MEMBER
 WHERE USER_NAME = #{userName}
   AND EMAIL = #{email}

SELECT *
  FROM MEMBER
 WHERE USER_Id = #{userId}
   AND EMAIL = #{email}

mapper.update:
UPDATE MEMBER
   SET USER_PWD = #{userPwd}
	 , USER_NICKNAME = #{userNickname}
	 , EMAIL = #{email}
	 , PHONE = #{phone}
	 , ADDRESS = #{address}
 WHERE USER_ID = #{userId}

UPDATE MEMBER
	  SET STATUS = 'N'
WHERE USER_ID = #{userId}

UPDATE PET
   SET USER_PWD = #{userPwd}
	 , USER_NICKNAME = #{userNickname}
	 , EMAIL = #{email}
	 , PHONE = #{phone}
	 , ADDRESS = #{address}
 WHERE USER_ID = #{userId}

