mapper.update:
UPDATE PETSITTER
   SET P_TITLE = #{petSitterTitle}
     , P_CONTENT = #{petSitterContent}
     , P_SERVICE = #{petSitterService}
     , CARE_LIST = #{careList}
     , P_MODE = #{petSitterMode}
 WHERE P_NO = #{petSitterNo}

UPDATE REVIEW
   SET A_CONTENT = #{acontent}
     , A_DATE = SYSDATE
 WHERE REV_NO = #{revNo}
