mapper.update:
UPDATE PETSITTER
   SET P_TITLE = #{petSitterTitle}
     , P_CONTENT = #{petSitterContent}
     , P_SERVICE = #{petSitterService}
     , CARE_LIST = #{careList}
     , P_MODE = #{petSitterMode}
 WHERE P_NO = #{petSitterNo}

UPDATE REVIEW
   SET A_CONTENT = #{acontent}
     , A_DATE = SYSDATE
 WHERE REV_NO = #{revNo}

UPDATE P_ATTACHMENT
   SET STATUS = 'N'
 WHERE FILE_NO = #{fileNo}

mapper.select:
SELECT COUNT(*)
  FROM P_LIKE
 WHERE USER_NO = #{userNo}
   AND REF_PNO = #{refPno}

mapper.delete:
DELETE FROM P_LIKE
 WHERE USER_NO = #{userNo}
   AND REF_PNO = #{refPno}

mapper.select:
SELECT COUNT(*)
  FROM P_LIKE
 WHERE REF_PNO = #{refPno}
